import React, { useRef } from 'react';
import './model-viewer.css';
import * as THREE from 'three';

const ViewerRightContainer = () => {
  const loader = useRef(null);
  const cubeWrapperRef = useRef(null);

  function hide(ele) {
    ele.style.display = 'none';
  }
  hide(loader);
  /** 3D cube */
  let cubeCameraDistance = 1.75;

	let cubeScene = new THREE.Scene();
	let cubeCamera = new THREE.PerspectiveCamera(70, cubeWrapperRef.offsetWidth / cubeWrapperRef.offsetHeight, 0.1, 100);
	let cubeRenderer = new THREE.WebGLRenderer({
		alpha: true,
		antialias: true,
		preserveDrawingBuffer: true
	}); 

	cubeRenderer.setSize(cubeWrapperRef.offsetWidth, cubeWrapperRef.offsetHeight);
	cubeRenderer.setPixelRatio(window.deivicePixelRatio);

	cubeWrapperRef.appendChild(cubeRenderer.domElement);

  return (
    <div>
      <div className='right-container'>
        <div id='backToHome'><i className='fa fa-home'></i></div>
        <div id='orientCubeWrapper' ref={cubeWrapperRef}></div>
      </div>
      <div className='share-sidebar' id='shareSidebar'>
        <h1 className='sidebar-title'>Some content here!</h1>
      </div>
      <div className='loader' id='loader' ref = {loader}>
        <div className='loader-text' id='loaderInfo'>Loading...</div> {/** add more padding for the Loading message */}
        <div className='loader-spinner'></div>
      </div>
    </div>
  );
};

export default ViewerRightContainer;
